import c from "./chatListItem.module.css"


function ChatListItem(props) {
    const MAX_EXCERPT_LENGTH = props.maxExcerptLength;
    const excerpt = (props.latestMessage<MAX_EXCERPT_LENGTH?props.latestMessage:(props.latestMessage.slice(0,MAX_EXCERPT_LENGTH) + "..."));

    const chatColorIconStyle = {
        width: "1rem",
        height: "1rem",
        borderRadius: "1rem",
        backgroundImage: `linear-gradient(135deg, ${props.chatColor[0]}, ${props.chatColor[1]})`
    }

    return (
        <>
            <hr className={c.separator} />
            <div className={c.chatListItemWrap}>
                <div 
                    style={chatColorIconStyle}
                    />
                <h4>{props.chatName}</h4>
                <div>
                    <p>{excerpt}</p>
                </div>
            </div>
        </>
    )
}

export default ChatListItem;
