import ChatListItem from "./ChatListItem";
import c from "../CSS/chatClasses.module.css";

function Sidebar(props) {
    async function setChatList() {
        const chats = await generateChatList();
        return chats;
    }
    function generateChatList() {
        const arr = [];
        for (let i = 0; i < props.chatData.length; i++) {
            arr [i] = 
            <ChatListItem
            key={props.chatData[i][0]}
            chatID={props.chatData[i][0]}
            chatName={props.chatData[i][1]}
            chatColor={props.chatData[i][2]}
            latestMessage={props.chatData[i][3]}
            setCurrentChatData={props.setCurrentChatData}
            />
        }
    }
    const chats = setChatList();
    return (
        <div className={c.sidebar}>
            <div className={c.sidebarHeader}>
                <h2>Chats</h2>
                <div><button type="button" className={c.createChatBtn} onClick={()=>{props.setCurrentChatData(-2, "Create Chat", ["#fff", "#fff"])}}></button></div>
            </div>
            <div className={c.messageList}>
                {props.isLoading?null:chats}
            </div>
        </div>
    );
}
export default Sidebar;
