import c from "../CSS/chatClasses.module.css"

function Message({sentBy, userID, chatColor, timeStamp, msg}) {

    const colorStyle = (userID===sentBy) ? {
        backgroundImage: `linear-gradient(135deg, ${chatColor[0]}, ${chatColor[1]})`
    } : {
        backgroundImage: `linear-gradient(135deg, #fff, #ccc)`
    };

    let time = "";
    time += (timeStamp.getHours()>11) ? timeStamp.getHours() - 10 : timeStamp.getHours();
    time += ":" + timeStamp.getMinutes();
    
    return(

        <div>
            <div className={c.timeStamp}>{time}</div>
            <div style={colorStyle}>
                {msg}
            </div>
        </div>
    )
}

export default Message;
