// Cameron McCarty
// 09/18/2023

import formClass from "./classes.module.css"

function FormInput(props) {
    if (props.inpType === "password") {
        return (
            <div className={formClass.labelGroup}>
            <label htmlFor="pwd">Password:</label>
            <input 
                className={props.isValid ? null : formClass.invalidInput}
                id="pwd" 
                type="passsword" 
                name="pwd"
                onChange={props.changeFunc}
                value={props.val}
                onBlur={props.blurFunc}
                />
            </div>
        )
    }
    if (props.inpType === "email") {
        return (
            <div className={formClass.labelGroup}>
            <label htmlFor="email">Email:</label>
            <input 
                className={props.isValid ? null : formClass.invalidInput}
                id="email" 
                type="email" 
                name="email"
                onChange={props.changeFunc}
                value={props.val}
                onBlur={props.blurFunc}
                />
            </div>
        )
    }
    if (props.inpType === "confpwd") {
        return (
            <div className={formClass.labelGroup}>
            <label htmlFor="confpwd">Confirm Password:</label>
            <input 
                className={props.isValid ? null : formClass.invalidInput}
                id="confpwd" 
                type="password" 
                name="confpwd"
                onChange={props.changeFunc}
                value={props.val}
                onBlur={props.blurFunc}
                />
            </div>
        )
    }
}

export default FormInput;
