import Sidebar from "./Sidebar";
import MenuTray from "./MenuTray";
import c from "../CSS/chatClasses.module.css";
import ChatWindow from "./ChatWindow";
import { useState, useEffect } from "react";


function Main({ user, userID }) {
    const MAIN_CONTENT_HEIGHT = window.innerHeight - 36;

    const [currentChatID, setCurrentChatID] = useState("closed");
    const [currentChatName, setCurrentChatName] = useState("");
    const [currentChatColor, setCurrentChatColor] = useState(["#fff","#fff"]);
    const [currentChatHistory, setCurrentChatHistory] = useState([]);

    console.log(currentChatID);

    //// Fetching backend data
    const [backendData, setBackendData] = useState([[0]]);
    const [chatData, setChatData] = useState([[0]]);
    const [chatHistories, setChatHistories] = useState([[]]);

    setChatHistories([[0, 0, 0, "", ""]]);
    console.log(chatHistories);

    fetch("/pullAPI").then(
        response => response.json()
    ).then(
        data => {
            setBackendData(data);
        }
    ).finally(
        parseBackendData()
    );
    // useEffect(() => {
    //     fetch("/pullAPI").then(
    //         response => response.json()
    //     ).then(
    //         data => {
    //             setBackendData(data);
    //         }
    //     ).finally(
    //         parseBackendData()
    //     );
    // }, []);

    function parseBackendData() {
        setChatHistories(backendData.chatHistories);
        setChatData(backendData.chatData);
    }
    for (let i = 0; i < chatHistories.length; i++) {
        if (currentChatID === chatHistories[i][1]) {
            console.log("match");
        }
    }
    
    // function getLatestMessages() {
    //     const latest = [];
    //     for (let i = 0; i < chatData.length; i++) {
    //         latest[i] = chatHistories.lastIndexOf();
    //     }
    // }

    function setCurrentChatData(x, y, z) {
        setCurrentChatID(x);
        setCurrentChatName(y);
        setCurrentChatColor(z);
    }

    return (
        <div className={c.mainWrap} style={{height: MAIN_CONTENT_HEIGHT}}>
            <MenuTray />
            <Sidebar
                userID={userID}
                setCurrentChatData={setCurrentChatData}
                />
            <div className={c.mainContent}>
                <ChatWindow
                            user={user}
                            chatID={currentChatID}
                            chatName={currentChatName}
                            chatColor={currentChatColor}
                            userID={userID}
                            setChatData={setCurrentChatData}
                            chatHistory={currentChatHistory}
                                />
            </div>
        </div>
    )
}

export default Main;
