import Sidebar from "./Sidebar";
import c from "./chatClasses.module.css";


function Main({ user }) {
    const MAIN_CONTENT_HEIGHT = window.innerHeight - document.querySelector("header").getBoundingClientRect().height;
    return (
        <div className={c.mainWrap} style={{height: MAIN_CONTENT_HEIGHT}}>
            <Sidebar />
            <div className={c.mainContent}>
                <h2>Welcome, {user}</h2>
                <p>Click on a chat to get started</p>
            </div>
        </div>
    )
}

export default Main;
