import EmojiMenu from "./EmojiMenu"
import c from "../CSS/chatClasses.module.css"
import { useState } from "react";

function ChatWindow({ chatID, chatName, chatColor, setChatID }) {
    // const chatColorStyle = {
    //     backgroundImage: `linear-gradient(135deg, ${chatColor[0]}, ${chatColor[1]})`
    // }
    const chatColorStyle = {
        backgroundImage: `linear-gradient(135deg, ${"#00ddff","#9900ff"})`
    }

    const getData = (chatID) => {
        // Function to get data pertaining to this chat
    } 

    if (chatID < 0) {
        return null;
    }
    else {

        return (
            <div className={c.chatWrapper}>
                <div className={c.chatHeader} style={chatColorStyle}>
                    <h2>{chatName}</h2>
                    <button className={c.chatClose} onClick={()=>{setChatID([-1, "", []])}}></button>
                </div>
                <div className={c.chatHistory}></div>

                <div className={c.messageBoxWrap}>
                    <textarea className={c.messageBox} name="msgBox" id="msgBox" placeholder="Type your message here..."></textarea>
                    {/* <EmojiMenu typeEmoji={typeEmoji} /> */}
                </div>
            </div>
        );
    }
}

export default ChatWindow;
