// import EmojiMenu from "./EmojiMenu
import c from "../CSS/chatClasses.module.css"
import Message from "./Message"

function ChatWindow({ chatID, chatName, chatColor, setChatData, userID }) {
    const chatColorStyle = {
        backgroundImage: `linear-gradient(135deg, ${chatColor[0]}, ${chatColor[1]})`
    }
    // Message ID, Chat ID, Sender ID, Time Stamp, Message
    const pulledMsgs = [
        [1, 2, 0, new Date(2023,10,6,18,53,30), "This is a test"],
        [2, 2, 2, new Date(2023,10,6,18,53,33), "I agree"],
        [3, 2, 2, new Date(2023,10,6,18,53,50), "..."],
        [4, 4, 3, new Date(2023,10,6,19,53,40), "This belongs on its own"],
        [5, 2, 0, new Date(2023,10,6,20,53,40), "Lorem ipsum, dolor sit amet consectetur adipisicing elit. Iure a, libero voluptatum illum corporis soluta nihil deleniti aliquam vitae, hic nisi. Omnis eum fugiat accusantium iste qui temporibus. Nulla, adipisci?"],
    ];

    const getData = () => {
        const msgs = []
        let c = 0;
        for (let i = 0; i < pulledMsgs.length; i++) {
            if (pulledMsgs[i][1] === chatID) {
                msgs[c] = pulledMsgs[i];
                c++;
            }
        }
        return msgs;
    }
    const msgs = getData().map((x) => {
        return  <Message
                    key={x[0]}
                    sentBy={x[2]}
                    userID={userID}
                    chatColor={chatColor}
                    timeStamp={x[3]}
                    msg={x[4]}
                    />
    });
    
    if (chatID < 1) {
        return (
            <div className={c.greeting}>
                    <div className={c.logo}></div>
                    <h2>Welcome, {user}</h2>
                    <p>Click on a chat to get started</p>
            </div>
        );
    }
    else {

        return (
            <div className={c.chatWrapper} style={chatColorStyle}>
                <div className={c.chatHeader}>
                    <h2>{chatName}</h2>
                    <button className={c.chatClose} onClick={()=>{setChatData(0, "", ["#fff", "#fff"]);}}></button>
                </div>
                <div className={c.chatHistory}>
                    {msgs}
                </div>

                <div className={c.messageBoxWrap}>
                    <div className={c.toolBar}></div>
                    <textarea className={c.messageBox} name="msgBox" id="msgBox" placeholder="Type your message here..."></textarea>
                    {/* <EmojiMenu typeEmoji={typeEmoji} /> */}
                </div>
            </div>
        );
    }
}

export default ChatWindow;
