import Sidebar from "./Sidebar";
import MenuTray from "./MenuTray";
import c from "../CSS/chatClasses.module.css";
import ChatWindow from "./ChatWindow";
import { useState } from "react";


function Main({ user }) {
    const MAIN_CONTENT_HEIGHT = window.innerHeight - 36;

    const [currentChatID, setCurrentChatID] = useState(-1); //// FIXME: Does not pass properly to children
    const [currentChatName, setCurrentChatName] = useState("");

    const setChatID = (x, y) => {setCurrentChatID(parseInt(x, 16)); setCurrentChatName(y);}

    return (
        <div className={c.mainWrap} style={{height: MAIN_CONTENT_HEIGHT}}>
            <MenuTray />
            <Sidebar setChatID={setChatID} />
            <div className={c.mainContent}>
                <div className={c.greeting}>
                    <div className={c.logo}></div>
                    <h2>Welcome, {user}</h2>
                    <p>Click on a chat to get started</p>
                </div>
                <ChatWindow chatID={currentChatID} chatName={currentChatName} />
            </div>
        </div>
    )
}

export default Main;
