function EmojiMenu() {
    const emojiCodes = [];

    function add(array, startCode, endCode, startIndex = emojiCodes.length) {
        let c = startIndex; //start index
        for (let i = startCode; i < endCode; i++) {
            array[c] = i;
            c++;
        }
    }
    add(128507, 128722, 0);
    add(128725, 128727);
    add(128733, 128748);
    add(128756, 128764);
    add(128992, 129008);
    add(129292, 129535);
    add(129648, 129653);
    add(129657, 129660);
    add(129664, 129671);
    add(129680, 129709);
    add(129713, 129722);
    add(129729, 129733);
    add(129745, 129753);
    add(129760, 129767);
    add(129776, 129782);

    function displayEmojis(e) {
        for (let i = 0; i < emojiCodes.length; i++) {
            e.target.value += (`&#${emojiCodes[i]}`);
        }
    }

    return(
        <div onrender={displayEmojis}>This is the emoji div </div>
    );
}

export default EmojiMenu;
