import ChatListItem from "./ChatListItem";
import c from "../CSS/chatClasses.module.css";

function Sidebar(props) {

    function setChat(x) {
        props.setChatID(x);
    }
    
    const MSG = "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Asperiores, porro voluptate consequatur quam debitis, tenetur voluptates, omnis eius distinctio incidunt mollitia animi nihil? Itaque, corporis dicta consectetur illo accusamus velit?"

    return (
        <div className={c.sidebar}>
            <div className={c.sidebarHeader}>
                <h2>Chats</h2>
                <button type="button" className={c.createChatBtn} onClick={props.createChat}></button>
            </div>
            <div className={c.messageList}>
                <ChatListItem
                    chatID={"1234567890"}
                    chatName={"Chat 1"}
                    chatColor={["#00ddff","#9900ff"]}
                    latestMessage={MSG}
                    setChatID={setChat}
                    />
                <ChatListItem
                    chatID={"1234567891"}
                    chatName={"Chat 2"}
                    chatColor={["#0000ff","#ff0000"]}
                    latestMessage={MSG}
                    
                    />
                <ChatListItem 
                    chatID={"1234567892"}
                    chatName={"Chat 3"}
                    chatColor={["#ffffff","#000000"]}
                    latestMessage={MSG}
                    />
            </div>
        </div>
    )
}
export default Sidebar;
