// Cameron McCarty
// 09/18/2023

import formClass from "./classes.module.css"

function PasswordInput() {
    return (
        <>
            <div className={formClass.labelGroup}>
                <label htmlFor="confpwd">Confirm Password:</label>
                <input 
                    className={p.validEmail ? null : formClass.invalidInput}
                    id="confpwd" 
                    type="passsword" 
                    name="confpwd"
                    onChange={p.handleConfPwd}
                    value={p.confpwd}
                    onBlur={p.validateConfPwd}
                    />
            </div>
            <p className={formClass.invalid} style={{display: p.validConfPwd ? "none" : "block"}}>Password does not match</p>
        </>
    )
}

export default PasswordInput;
