import Sidebar from "./Sidebar";
import MenuTray from "./MenuTray";
import c from "../CSS/chatClasses.module.css";
import ChatWindow from "./ChatWindow";
import { useState } from "react";


function Main({ user, userID }) {
    const MAIN_CONTENT_HEIGHT = window.innerHeight - 36;

    const [currentChatData, setcurrentChatData] = useState([0, ""]);


    const setChatData = (x) => {
        setcurrentChatData(parseInt(x[0], 16), x[1], x[2]);
    }

    return (
        <div className={c.mainWrap} style={{height: MAIN_CONTENT_HEIGHT}}>
            <MenuTray />
            <Sidebar
                userID={userID}
                setChatData={setChatData}
                />
            <div className={c.mainContent}>
                <div className={c.greeting}>
                    <div className={c.logo}></div>
                    <h2>Welcome, {user}</h2>
                    <p>Click on a chat to get started</p>
                </div>
                <ChatWindow chatID={currentChatData[0]}
                            chatName={currentChatData[1]}
                            setChatData={setChatData}
                                />
            </div>
        </div>
    )
}

export default Main;
