// Cameron McCarty
// 09/11/2023


import { useState } from 'react';
import formClass from './classes.module.css';
import FormInput from './FormInput';

// Dev console.log function
// const l = (...x) => {for (let i = 0; i < x.length; i++) {console.log(x[i]);};};

function SignInForm() {
    const [email, setEmail] = useState("");
    const [pwd, setPwd] = useState("");
    const [confpwd, setConfPwd] = useState("");

    const [newUser, setSignInState] = useState(false);
    const [forgotPassword, setForgot] = useState(false);
    const [emailSent, setEmailSent] = useState(false);
    const [validEmail, setEmailValidation] = useState(true);
    const [validPwd, setValidPwd] = useState(true);
    const [pwdFailState, setPwdFailState] = useState(<></>);
    const [validConfPwd, setValidConfPwd] = useState(true);

    // Regex Email validator found here: https://stackoverflow.com/questions/46155/how-can-i-validate-an-email-address-in-javascript
    const REGEX_MATCH = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|.(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

    function handleEmail(e) {
        setEmail(e.target.value);
    }
    function handlePwd(e) {
        setPwd(e.target.value);
    }
    function handleConfPwd(e) {
        setConfPwd(e.target.value);
    }
    function validatePwd(e) {
        if (e.target.value === "") {
            return;
        }
        let password = e.target.value;
        let i = 0;
        let checks = [false, false, false]; // Upper, Lower, Number
        while (i < password.length && !(checks[0] && checks[1] && checks[2])) {
            let x = password.charCodeAt(i);
            if (x >= 65 && x <= 90) { checks[0] = true; }
            else if (x >= 97 && x <= 122) { checks[1] = true; }
            else if (x >= 48 && x <= 57) { checks[2] = true; }
            i++;
        }
        setValidPwd(true);
        if (password === "") {
            setPwdFailState(<></>);
        }
        else {
            setPwdFailState(
                <>
                    <p>Password requires the following:</p>
                    <ul>
                        {(password.length < 8 || password.length > 20) ? <li>Must be between 8-20 characters</li> : setValidPwd(false)}
                        {!checks[0] ? <li>An Uppercase Letter</li> : setValidPwd(false)}
                        {!checks[1] ? <li>A Lowercase Letter</li> : setValidPwd(false)}
                        {!checks[2] ? <li>A number</li> : setValidPwd(false)}
                    </ul>
                </>
            );
        }
        if (!checks[0] || !checks[1] || !checks[2]) {
            setValidPwd(false);
        }
    }

    function validateEmail(e) {
        if (e.target.value === "") {
            return;
        }
        if (e.target.value.toLowerCase().match(REGEX_MATCH)) {
            setEmailValidation(true);
            return;
        }
        setEmailValidation(false);
    }
    function validateConfPwd(e) {
        if (e.target.value === "") {
            return;
        }
        if (e.target.value === pwd) {
            setValidConfPwd(true);
            return;
        }
        setValidConfPwd(false);
    }


    function handleSubmit(e) {
        e.preventDefault();
        if (email === "" || pwd === "" || confpwd === "") {
            return;
        }

        if (forgotPassword) {
            setEmailSent(true);
            // Email-sending code
            return;
        }
        
        // Temporary alert to confirm that this works
        alert(`DEV MESSAGE:\nSubmitted Email: ` + email + `\nSubmitted Password: ` + pwd + `\nNew user? ` + newUser);
        if (newUser) {
            // Commit values to database
        }
        else {
            // Check database for values to retrieve returning user's data
        }
        clearInputs();
    }
    function clearInputs() {
        setEmail("");
        setPwd("");
        setConfPwd("");
        setEmailSent(false);
        setPwdFailState(<></>);
        setEmailValidation(true);
        setValidPwd(true);
        setValidConfPwd(true);
    }
    function swapForm() {
        setSignInState(!newUser);
        clearInputs();
    }
    function forgotPwd() {
        setForgot(!forgotPassword);
        clearInputs();
    }
    if (forgotPassword) {
        if (emailSent) {
            return (
                <div id='formWrapper' className={formClass.loginForm}>
                <div className={formClass.formSwapper}>
                    <button className={formClass.forgotPwdBackButton} onClick={forgotPwd}>Go Back</button>
                </div>
                <div className={formClass.formLogo}></div>
                    <h2>Email Sent!</h2>
                    <div className={formClass.confirmation}>
                        <p>An email has been sent to: {email}. The email will be sent within the next few minutes. Please follow the instructions in the email once you receive it.</p>
                    </div>
                </div>
            )
        }
        else {
            return (
                <div id='formWrapper' className={formClass.loginForm}>
                <div className={formClass.formSwapper}>
                    <button className={formClass.forgotPwdBackButton} onClick={forgotPwd}>Go Back</button>
                </div>
                <div className={formClass.formLogo}></div>
                <h2>Forgot Password</h2>

                <form id='SignUpForm' onSubmit={handleSubmit}>
                    <div className={formClass.formInput}>
                        <FormInput
                            inpType="email"
                            changeFunc={handleEmail}
                            val={email}
                            blurFunc={validateEmail}
                            isValid={validEmail}
                            />
                    </div>
                        <button className={formClass.submitBtn} type='submit'>Send</button>
                    </form>
                </div>
            )
        }
    }
    else if (newUser) {
        return (
            <div id='formWrapper' className={formClass.loginForm}>
                <div className={formClass.formSwapper}>
                    <h3>Returning User? </h3>
                    <button onClick={swapForm}>Sign In</button>
                </div>
                <div className={formClass.formLogo}></div>
                <h2>Sign Up</h2>

                <form id='SignUpForm' onSubmit={handleSubmit}>
                    <div className={formClass.formInput}>
                        <FormInput
                            inpType="email"
                            changeFunc={handleEmail}
                            val={email}
                            blurFunc={validateEmail}
                            isValid={validEmail}
                            />
                        <p className={formClass.invalid} style={{display: validEmail ? "none" : "block"}}>Invalid Email</p>
                    </div>
                    <div className={formClass.formInput}>
                        <FormInput
                            inpType={"password"}
                            changeFunc={handlePwd}
                            val={pwd}
                            blurFunc={validatePwd}
                            isValid={validPwd}
                            />
                        <div className={formClass.invalid} style={{display: validPwd ? "none" : "block"}}>{pwdFailState}</div>
                    </div>
                    <div className={formClass.formInput}>
                        <FormInput 
                            inpType="confpwd"
                            name="confpwd"
                            changeFunc={handleConfPwd}
                            val={confpwd}
                            blurFunc={validateConfPwd}
                            isValid={validConfPwd}
                            />
                        {validConfPwd ? null : <div className={formClass.invalid}>Password does not match</div>}
                    </div>
                    <button className={formClass.submitBtn} type='submit'>Sign Up</button>
                </form>
            </div>
        )
    }
    else {
        return (
            <div id='formWrapper' className={formClass.loginForm}>
                <div className={formClass.formSwapper}>
                    <h3>New here? </h3>
                    <button onClick={swapForm}>Sign Up</button>
                </div>
                <div className={formClass.formLogo}></div>
                <h2>Sign In</h2>

                <form id='SignInForm' onSubmit={handleSubmit}>
                    <div className={formClass.formInput}>
                        <FormInput
                            inpType="email"
                            changeFunc={handleEmail}
                            val={email}
                            blurFunc={validateEmail}
                            isValid={validEmail}
                            />
                    </div>
                    <div className={formClass.formInput}>
                        <FormInput
                        inpType="password"
                            changeFunc={handlePwd}
                            val={pwd}
                            blurFunc={validatePwd}
                            isValid={validPwd}
                            />
                    </div>
                    <div className={formClass.forgotPwdSection}>
                        <p>Forgot password?</p>
                        <button className={formClass.forgotPwdButton} onClick={forgotPwd}>Reset Password</button>
                    </div>
                    <button className={formClass.submitBtn} type='submit'>Sign in</button>
                </form>
            </div>
        )
    }
}

export default SignInForm;
