import Sidebar from "./Sidebar";
import MenuTray from "./MenuTray";
import c from "../CSS/chatClasses.module.css";
import ChatWindow from "./ChatWindow";
import { useState, useEffect } from "react";


function Main({ user, userID }) {
    const MAIN_CONTENT_HEIGHT = window.innerHeight - 36;

    const [currentChatID, setCurrentChatID] = useState("closed");
    const [currentChatName, setCurrentChatName] = useState("");
    const [currentChatColor, setCurrentChatColor] = useState(["#fff","#fff"]);


    //// Fetching backend data
    const [backendData, setBackendData] = useState([{}]);
    const [chatData, setChatData] = useState([{}]);
    const [chatHistories, setChatHistories] = useState([{}]);

    useEffect(() => {
        fetch("/pullAPI").then(
            response => response.json()
        ).then(
            data => {
                setBackendData(data);
            }
        ).finally(
            parseBackendData()
        );
    }, []);

    function parseBackendData() {
        setChatData(backendData.chatData);
        setChatHistories(backendData.chatHistories);
    }

    function getChats() {
        // Get all data pertaining to the chats
    }

    // const chatData = getChats(); // Switch to this once backend connection is established

    // Placeholder content that will be replaced by backend data
    // Chat ID, Chat name, Chat color gradient
    // setChatData([
    //     [1,"Announcements",["#00ddff","#9900ff"]],
    //     [2,"Chat 2",["#0000ff","#ff0000"]],
    //     [3,"Chat 3",["#ffffff","#000000"]],
    //     [4,"Chat 4",["#00ff00","#000000"]],
    // ]);
    function getLatestMessages() {
        const latest = [];
        for (let i = 0; i < chatData.length; i++) {
            latest[i] = chatHistories.lastIndexOf();
        }
    }

    function getChatHistories() {
        // Get all chat histories pertaining to this user
    }

    // const chatHistories = getChatHistories(); // Switch to this once backend connection is established

    // Placeholder content that will be replaced by backend data
    // Message ID, Chat ID, Sender ID, Time Stamp, Message
    // setChatHistories([
    //     [1, 2, 0, new Date(2023,10,6,18,53,30), "This is a test"],
    //     [2, 2, 2, new Date(2023,10,6,18,53,33), "I agree"],
    //     [3, 2, 2, new Date(2023,10,6,18,53,50), "..."],
    //     [4, 4, 3, new Date(2023,10,6,19,53,40), "This belongs on its own"],
    //     [5, 2, 0, new Date(2023,10,6,20,53,40), "Lorem ipsum, dolor sit amet consectetur adipisicing elit. Iure a, libero voluptatum illum corporis soluta nihil deleniti aliquam vitae, hic nisi. Omnis eum fugiat accusantium iste qui temporibus. Nulla, adipisci?"],
    //     [6, 2, 3, new Date(2023,10,6,20,53,41), "The endoplasmic reticulum does not harbor an ATP-regeneration machinery, and therefore requires ATP import from mitochondria. The imported ATP is vital for the ER to carry out its house keeping cellular functions, such as for protein folding and trafficking."],
    //     [7, 2, 0, new Date(2023,10,6,20,53,42), "Men's synchronized 10 metre platform event at the 2019 European Diving Championships was contested on 8 August."],
    //     [8, 2, 0, new Date(2023,10,6,20,53,43), "No railway was ever built through Oteramika, but at one point, the Kapuka railway station on the Tokanui Branch was named Oteramika. This station opened on 1 March 1895, closed to passengers on 1 June 1960, and closed to all traffic on 31 March 1966."],
    //     [9, 2, 0, new Date(2023,10,6,20,53,44), `Portlaoise were the defending champions after they defeated Ballylinan in the previous years final. They successfully defended their title to claim a "2-in-a-row" of victories when they defeated O'Dempsey's in this years final.\nEmo made a straight return to the senior grade after just one year outside the top flight.\nThis was the second season running in which Clonaslee–St Manman's amalgamated with Annanough for their championship matches as Clonaslee Gaels (however unlike 2017, Annanough didn't field a side in the 2018 I.F.C.). Both clubs fielded separate reserve sides in the Laois J.F.C. 'C'. The amalgamation suffered relegation at the end of the 2018 campaign and so will ply their trade in the 2019 I.F.C.`],
    // ]);

    function setCurrentChatData(x, y, z) {
        setCurrentChatID(x);
        setCurrentChatName(y);
        setCurrentChatColor(z);
    }

    return (
        <div className={c.mainWrap} style={{height: MAIN_CONTENT_HEIGHT}}>
            <MenuTray />
            <Sidebar
                userID={userID}
                setCurrentChatData={setCurrentChatData}
                />
            <div className={c.mainContent}>
                <ChatWindow
                            user={user}
                            chatID={currentChatID}
                            chatName={currentChatName}
                            chatColor={currentChatColor}
                            userID={userID}
                            setChatData={setCurrentChatData}
                            chatHistories={chatHistories}
                                />
            </div>
        </div>
    )
}

export default Main;
