import { useState, useEffect } from 'react';
import Header from './comp/MCCARTY/Header.js';
import Main from './comp/MCCARTY/Main.js';
import SignUpForm from './comp/MCCARTY/SignInForm.js';
import appStyle from './comp/MCCARTY/classes.module.css';
import GeneralCSS from "./comp/MCCARTY/general.css";

function App() {
    const [signedIn, setSignInState] = useState(false);
    const [userName, setUserName] = useState("");
    const [userPassword, setUserPassword] = useState("");

    if (signedIn) {
        return (

            <div>
                <Header />
                <Main user={userName} />
            </div>
        );
    }
    else {
        return (
            <div className={appStyle.loginScreenWrapper}>
                <Header />
                <SignUpForm
                    setUser={setUserName}
                    setPassword={setUserPassword}
                    isSignedIn={setSignInState}
                    />
            </div>
        );
    }
}

export default App;
