// import EmojiMenu from "./EmojiMenu
import c from "../CSS/chatClasses.module.css"
import Message from "./Message"

function ChatWindow({ chatID, chatName, chatColor, setChatData, userID }) {
    const chatColorStyle = {
        backgroundImage: `linear-gradient(135deg, ${chatColor[0]}, ${chatColor[1]})`
    }
    const pulledMsgs = [
        [1, 0, 2, "This is a test"],
        [2, 1, 2, "I agree"],
        [3, 3, 2, "..."],
        [4, 2, 3, "This belongs on its own"],
    ];

    const getData = () => {
        const msgs = []
        let c = 0;
        for (let i = 0; i < pulledMsgs.length; i++) {
            if (pulledMsgs[i][1] === chatID) {
                msgs[c] = pulledMsgs[i];
                c++;
            }
        }
        return msgs;
    }
    const msgs = getData().map((x) => {
        return  <Message
                    sentBy={x[1]}
                    userID={userID}
                    chatColor={chatColor}>{x[3]}
                </Message>
    });
    
    
    if (chatID < 1) {
        return null;
    }
    else {

        return (
            <div className={c.chatWrapper} style={chatColorStyle}>
                <div className={c.chatHeader}>
                    <h2>{chatName}</h2>
                    <button className={c.chatClose} onClick={()=>{setChatData(0, "", ["#fff", "#fff"]);}}></button>
                </div>
                <div className={c.chatHistory}>
                    <Message
                        sentBy={1}
                        userID={userID}
                        chatColor={chatColor}
                        >This is a test</Message>
                </div>

                <div className={c.messageBoxWrap}>
                    <div className={c.toolBar}></div>
                    <textarea className={c.messageBox} name="msgBox" id="msgBox" placeholder="Type your message here..."></textarea>
                    {/* <EmojiMenu typeEmoji={typeEmoji} /> */}
                </div>
            </div>
        );
    }
}

export default ChatWindow;
