import c from "../CSS/chatClasses.module.css"

function Message({chatColor, userID, sentBy, msg}) {

    const colorStyle = (userID===sentBy) ? {
        backgroundImage: `linear-gradient(135deg, ${chatColor[0]}, ${chatColor[1]})`
    } : {
        backgroundImage: `linear-gradient(135deg, "#fff", "#ccc")`
    };

    return(

        <div style={colorStyle}>{msg}</div>
    )
}

export default Message;
