// Cameron McCarty
// 09/11/2023


import { useState } from 'react';
import formClass from './classes.module.css';
import FormInput from './FormInput';

// Dev console.log function
const l = (...x) => {for (let i = 0; i < x.length; i++) {console.log(x[i]);};};

function SignInForm() {
    const [email, setEmail] = useState("");
    const [pwd, setPwd] = useState("");
    const [confpwd, setConfPwd] = useState("");

    const [newUser, setSignInState] = useState(false);
    const [forgotPassword, setForgot] = useState(false);
    const [emailSent, setEmailSent] = useState(false);
    const [validEmail, setEmailValidation] = useState(true);
    const [validPwd, setValidPwd] = useState([true, true, true, true]);
    const [pwdFailState, setPwdFailState] = useState(<></>);
    const [validConfPwd, setValidConfPwd] = useState(true);

    // Regex Email validator found here: https://stackoverflow.com/questions/46155/how-can-i-validate-an-email-address-in-javascript
    const REGEX_MATCH = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|.(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    const invalid = pwdFailState;

    function handleEmail(e) {
        setEmail(e.target.value);
    }
    function handlePwd(e) {
        setPwd(e.target.value);
    }
    function handleConfPwd(e) {
        setConfPwd(e.target.value);
    }




    function validatePwd(e) {
        const password = e.target.value;
        setValidPwd([false, false, false, false]);
        if (password === "") {
            setPwdFailState(<></>);
            // Length, Upper, Lower, Number, All
            setValidPwd([true, true, true, true, true]);
            return;
        }
        let MeetsLenReq = false,
            MeetsUpperReq = false,
            MeetsLowerReq = false,
            MeetsNumReq = false,
            MeetsAllReq = false;

        if (password.length >= 8 && password.length <= 20) { MeetsLenReq = true; }
        for (let i = 0; i < password.length; i++) {
            if (password.charCodeAt(i) >= 65 && password.charCodeAt(i) <= 90) { MeetsUpperReq = true; }
            if (password.charCodeAt(i) >= 97 && password.charCodeAt(i) <= 122) { MeetsLowerReq = true; }
            if (password.charCodeAt(i) >= 48 && password.charCodeAt(i) <= 57) { MeetsNumReq = true; }
            if (MeetsLenReq && MeetsUpperReq && MeetsLowerReq && MeetsNumReq) { MeetsAllReq = true; break; }
        }

        setValidPwd([MeetsLenReq, MeetsUpperReq, MeetsLowerReq, MeetsNumReq, MeetsAllReq]);


