// Cameron McCarty
// 09/11/2023

const l = (...x) => {for (let i = 0; i < x.length; i++) {console.log(x[i]);};};

import logo from '../../media/t2meLogo.png';
import { useState } from 'react';
import formClass from './classes.module.css';

function SignInForm() {
    const [user, setUser] = useState("");
    const [pwd, setPwd] = useState("");
    const [confpwd, setConfPwd] = useState("");
    const [newUser, setSignInState] = useState(false);
    const [forgotPassword, setForgot] = useState(false);
    const [validEmail, setEmailValidation] = useState(true);
    const [validPwd, setValidPwd] = useState(true);

    function handleEmail(e) {
        setUser(e.target.value);
    }
    function handlePwd(e) {
        setPwd(e.target.value);
    }
    function handleConfPwd(e) {
        setConfPwd(e.target.value);
    }

    function validateEmail(e) {
        // Emails cannot have spaces
        let email = e.target.value;
        if (e.target.value.contains(" ") || email.contains("\t") || email.contains("\n")) {
            setEmailValidation(false);
            return;
        }
        email = email.trim();
        // Makes sure the email field is not empty, has an @ symbol and a .
        if (email === "" || !(email.includes("@") && email.includes("."))) {
            setEmailValidation(false);
            return;
        }
        setEmailValidation(true);
    }


    function handleSubmit(e) {
        e.preventDefault();
        let inp1 = document.querySelector("#userName").value; // Values stored here for submission into the database. Still needs validation
        let inp2 = document.querySelector("#pwd").value;
        let inp3 = "";
        if (newUser) {
            inp3 = document.querySelector("#confpwd").value;
        }
        //// Basic Validation. Will need backend validation
        if ( // Simple validation
        // Ensures password meets length requirements 
         inp2.length < 8 || inp2.length > 20 
        // Password and confirmation match
        || (newUser && inp3 !== inp2)) {
            alert("Invalid input.");
            return;
        }
        let i = 0;
        let checks = [false, false, false]; // Upper, Lower, Number
        while (i < inp2.length && !(checks[0] && checks[1] && checks[2])) {
            let x = inp2.charCodeAt(i);
            if (x >= 65 && x <= 90) { checks[0] = true; }
            else if (x >= 97 && x <= 122) { checks[1] = true; }
            else if (x >= 48 && x <= 57) { checks[2] = true; }
            i++;
        }
        if (!(checks[0] && checks[1] && checks[2])) {
            alert("Invalid input.");
            return;
        }
        //// End of Validation

        // Temporary alert to confirm that this works
        alert(`DEV MESSAGE:\nSubmitted User Name: ` + inp1 + `\nSubmitted Password: ` + inp2 + `\nNew user? ` + newUser);
        if (newUser) {
            // Commit values to database
        }
        else {
            // Check database for values to retrieve returning user's data
        }
        setUser("");
        setPwd("");
        setConfPwd("");
    }
    function swapForm() {
        setSignInState(!newUser);
        setUser("");
        setPwd("");
        setConfPwd("");
    }
    function forgotPwd() {
        setForgot(!forgotPassword);
    }
    if (forgotPassword) {
        return (
            <div id='formWrapper' className={formClass.loginForm}>
                <div className={formClass.formSwapper}>
                    <button className={formClass.forgotPwdBackButton} onClick={forgotPwd}>Go Back</button>
                </div>
                <div className={formClass.formLogo}></div>
                <h2>Forgot Password</h2>

                <form id='SignUpForm' onSubmit={handleSubmit}>
                    <div className={formClass.formInput}>
                        <label htmlFor="userName">Email:</label>
                        <input 
                            id="userName" 
                            type="text" 
                            name="userName"
                            onChange={handleEmail}
                            value={user}
                            onBlur={validateEmail}
                            />
                    </div>
                    <button className={formClass.submitBtn} type='submit'>Send</button>
                </form>
            </div>
        )
    }
    else if (newUser) {
        return (
            <div id='formWrapper' className={formClass.loginForm}>
                <div className={formClass.formSwapper}>
                    <h3>Returning User? </h3>
                    <button onClick={swapForm}>Sign In</button>
                </div>
                <div className={formClass.formLogo}></div>
                <h2>Sign Up</h2>

                <form id='SignUpForm' onSubmit={handleSubmit}>
                    <div className={formClass.formInput}>
                        <label htmlFor="userName">Email:</label>
                        <input 
                            id="userName" 
                            type="text" 
                            name="userName"
                            onChange={handleEmail}
                            value={user}
                            />
                        <p style={{display: validEmail ? "none" : "block"}}>Invalid Email</p>
                    </div>
                    <div className={formClass.formInput}>
                        <label htmlFor="pwd">Password:</label>
                        <input 
                            id="pwd" 
                            type="passsword" 
                            name="pwd"
                            onChange={handlePwd}
                            value={pwd}
                            />
                    </div>
                    <div className={formClass.formInput}>
                        <label htmlFor="confpwd">Confirm Password:</label>
                        <input 
                            id="confpwd" 
                            type="passsword" 
                            name="confpwd"
                            onChange={handleConfPwd}
                            value={confpwd}
                            />
                    </div>
                    <button className={formClass.submitBtn} type='submit'>Sign Up</button>
                </form>
            </div>
        )
    }
    else {
        return (
            <div id='formWrapper' className={formClass.loginForm}>
                <div className={formClass.formSwapper}>
                    <h3>New here? </h3>
                    <button onClick={swapForm}>Sign Up</button>
                </div>
                <div className={formClass.formLogo}></div>
                <h2>Sign In</h2>

                <form id='SignInForm' onSubmit={handleSubmit}>
                    <div className={formClass.formInput}>
                        <label htmlFor="userName">Email:</label>
                        <input 
                            id="userName" 
                            type="text" 
                            name="userName"
                            onChange={handleEmail}
                            value={user}
                            />
                    </div>
                    <div className={formClass.formInput}>
                        <label htmlFor="pwd">Password:</label>
                        <input 
                            id="pwd" 
                            type="password" 
                            name="pwd"
                            onChange={handlePwd} 
                            value={pwd}
                            />
                    </div>
                    <div className={formClass.forgotPwdSection}>
                        <p>Forgot password?</p>
                        <button className={formClass.forgotPwdButton} onClick={forgotPwd}>Reset Password</button>
                    </div>
                    <button className={formClass.submitBtn} type='submit'>Sign in</button>
                </form>
            </div>
        )
    }
}

export default SignInForm;
