import ChatListItem from "./ChatListItem";
import c from "../CSS/chatClasses.module.css";

function Sidebar(props) {

    const MSG = "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Asperiores, porro voluptate consequatur quam debitis, tenetur voluptates, omnis eius distinctio incidunt mollitia animi nihil? Itaque, corporis dicta consectetur illo accusamus velit?"
    function getData() {
        // code to get user data based on props.userID
        return standInData();
    }
    function standInData() {
        return [
            ["0000000000000001", "Chat 1", ["#00ddff","#9900ff"], MSG],
            ["000000A234567891", "Chat 2", ["#0000ff","#ff0000"], MSG],
            ["000000BB34567892", "Chat 3", ["#ffffff","#000000"], MSG],
        ];
    }

    const chats = getData().map((x) => {
        return <ChatListItem
                chatID={x[0]}
                chatName={x[1]}
                chatColor={x[2]}
                latestMessage={x[3]}
                setChatData={setChatData}
                    />
    });


    const setChatData = (x, y, z) => {
        props.setChatData(x, y, z);
        console.log(`Sidebar fired \nchatData: ${x} | ${y} | ${z}`);
    }
    

    return (
        <div className={c.sidebar}>
            <div className={c.sidebarHeader}>
                <h2>Chats</h2>
                <button type="button" className={c.createChatBtn} onClick={props.createChat}></button>
            </div>
            <div className={c.messageList}>
                {chats}
                {/* <ChatListItem
                    chatID={}
                    chatName={}
                    chatColor={}
                    latestMessage={}
                    setChatData={setChatData}
                    /> */}
            </div>
        </div>
    )
}
export default Sidebar;
