import c from "../CSS/chatClasses.module.css"

function Message({sentBy, userID, chatColor, timeStamp, msg}) {

    const colorStyle = (userID===sentBy) ? {
        backgroundImage: `linear-gradient(135deg, ${chatColor[0]}, ${chatColor[1]})`,
    } : {
        backgroundImage: `radial-gradient(135deg, rgba(255,255,255,0.1), rgba(128,128,128,0.5))`,
    };

    const time = ((timeStamp.getHours()>11) ? timeStamp.getHours() - 10 : timeStamp.getHours()) + ":" + timeStamp.getMinutes();
    
    return(

        <div className={(userID===sentBy) ? c.msgWrap1 : c.msgWrap2}>
            <div className={c.timeStamp}><p>{time}</p></div>
            <div style={colorStyle} className={c.msg}>
                <p>{msg}</p>
            </div>
        </div>
    )
}

export default Message;
